import React from 'react'
import ReactDOM from 'react-dom'

const Header = (props) => {
    return (
        <div>
            <h1>{props.course}</h1>
        </div>
    )
}

const Part = (props) => {
    return (
        <div>
            <p>
                {props.part} {props.exercises}
            </p>

        </div>
    )
}

const Content = (props) => {
    return (
        <div>
            <Part part={props.part_1} exercises={props.exercises1} />
            <Part part={props.part_2} exercises={props.exercises2} />
            <Part part={props.part_3} exercises={props.exercises3} />
        </div>
    )
}

const Total = (props) => {
    return (
        <div>
            <p>Number of exercises {props.exercises1 + props.exercises2 + props.exercises3}</p>
        </div>
    )
}

const App = () => {

    const course = 'Half Stack application development'
    const parts = [
        {
            name: 'Fundamentals of React',
            exercises: 10
        },
        {
            name: 'Using props to pass data',
            exercises: 7
        },
        {
            name: 'State of a component',
            exercises: 14
        }
    ]



    return (
        <div>
            <Header course={course} />
            <Content
                part_1={parts[0].name} exercises1={parts[0].exercises}
                part_2={parts[1].name} exercises2={parts[1].exercises}
                part_3={parts[2].name} exercises3={parts[2].exercises}
            />
            <Total
                exercises1={parts[0].exercises}
                exercises2={parts[1].exercises}
                exercises3={parts[2].exercises}
            />
        </div>
    )

}

ReactDOM.render(<App />, document.getElementById('root'))
